def find_start_of_packet_marker(datastream_buffer):
  # Keep track of the last four characters that were processed.
  last_four_chars = []

  # Iterate through the datastream buffer character by character.
  for i in range(len(datastream_buffer)):
    # Add the current character to the last_four_chars list.
    last_four_chars.append(datastream_buffer[i])

    # If the last_four_chars list has more than four elements, remove the oldest one.
    if len(last_four_chars) > 4:
      last_four_chars.pop(0)

    # If the last_four_chars list contains four different characters,
    # we have found our start-of-packet marker.
    if len(set(last_four_chars)) == 4:
      return i + 1 # Return the number of characters processed before we found the marker.

# Test the find_start_of_packet_marker() function with the example from the problem statement.
datastream_buffer = ""
result = find_start_of_packet_marker(datastream_buffer)
# The result should be 7, because the first start-of-packet marker is complete after 7 characters have been processed.
print(result)
